import React, { useState, useEffect } from 'react';
import { 
  ComposedChart, 
  Line, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend, 
  ResponsiveContainer 
} from 'recharts';
import { fetchCombinedStockChart, fetchStockChartSummary, fetchEnhancedStockInfo } from '../../api/stockChart';
import './StockChart.css';

const StockChart = ({ symbol, startDate, endDate }) => {
  const [chartData, setChartData] = useState([]);
  const [chartSummary, setChartSummary] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [selectedPeriod, setSelectedPeriod] = useState('6M');
  const [showPeriodOptions, setShowPeriodOptions] = useState(false);

  const periodOptions = [
    { value: '1W', label: '1Ï£º' },
    { value: '1M', label: '1Í∞úÏõî' },
    { value: '3M', label: '3Í∞úÏõî' },
    { value: '6M', label: '6Í∞úÏõî' },
    { value: '1Y', label: '1ÎÖÑ' }
  ];

  // Í∏∞Í∞ÑÏóê Îî∞Î•∏ ÎÇ†Ïßú Í≥ÑÏÇ∞
  const calculateDateRange = (period, endDate) => {
    const end = new Date(endDate);
    const start = new Date(end);

    switch (period) {
      case '1W':
        start.setDate(end.getDate() - 7);
        break;
      case '1M':
        start.setMonth(end.getMonth() - 1);
        break;
      case '3M':
        start.setMonth(end.getMonth() - 3);
        break;
      case '6M':
        start.setMonth(end.getMonth() - 6);
        break;
      case '1Y':
        start.setFullYear(end.getFullYear() - 1);
        break;
      default:
        start.setMonth(end.getMonth() - 1);
    }

    return {
      startDate: start.toISOString().split('T')[0],
      endDate: end.toISOString().split('T')[0]
    };
  };

  // Ï∞®Ìä∏ Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  const loadChartData = async () => {
    if (!symbol) return;

    setLoading(true);
    setError('');

    try {
      const { startDate: calcStartDate, endDate: calcEndDate } = calculateDateRange(selectedPeriod, endDate);
      const fixedChartTypes = ['price', 'volume'];

      console.log('üöÄ Loading chart data:', { 
        symbol, 
        period: selectedPeriod,
        types: fixedChartTypes, 
      });

      // Ï∞®Ìä∏ Îç∞Ïù¥ÌÑ∞, ÏöîÏïΩ Ï†ïÎ≥¥, ÏÉÅÏÑ∏ Ï†ïÎ≥¥Î•º ÎèôÏãúÏóê Í∞ÄÏ†∏Ïò§Í∏∞
      const [data, summaryData, enhancedData] = await Promise.all([
        fetchCombinedStockChart(
          symbol, 
          calcStartDate, 
          calcEndDate, 
          fixedChartTypes,
        ),
        fetchStockChartSummary(symbol, calcStartDate, calcEndDate),
        fetchEnhancedStockInfo(symbol)
      ]);

      console.log('üì¶ Received chart data:', data);
      console.log('üìà Received enhanced data:', enhancedData);

      // Ï∞®Ìä∏ Îç∞Ïù¥ÌÑ∞ Î≥ÄÌôò
      const transformedData = data.dates.map((date, index) => {
        const item = { date };

        // Ï£ºÍ∞Ä Îç∞Ïù¥ÌÑ∞
        if (data.data.price) {
          item.close = data.data.price.closes[index];
          item.open = data.data.price.opens[index];
          item.high = data.data.price.highs[index];
          item.low = data.data.price.lows[index];
        }

        // Í±∞ÎûòÎüâ Îç∞Ïù¥ÌÑ∞
        if (data.data.volume) {
          item.volume = data.data.volume.volumes[index];
        }

        return item;
      });

      console.log('üéØ Transformed data sample:', transformedData.slice(0, 3));

      // ÏÉÅÏÑ∏ Ï†ïÎ≥¥Î•º ÏöîÏïΩ Ï†ïÎ≥¥Ïóê Î≥ëÌï©
      const mergedSummary = {
        ...summaryData,
        ...enhancedData
      };

      setChartData(transformedData);
      setChartSummary(mergedSummary);
    } catch (err) {
      setError('Ï∞®Ìä∏ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§: ' + err.message);
      console.error('Chart data loading error:', err);
    } finally {
      setLoading(false);
    }
  };

  // Í∏∞Í∞ÑÏù¥ÎÇò Ï∞®Ìä∏ ÌÉÄÏûÖÏù¥ Î≥ÄÍ≤ΩÎê† Îïå Îç∞Ïù¥ÌÑ∞ ÏÉàÎ°úÍ≥†Ïπ®
  useEffect(() => {
    loadChartData();
  }, [symbol, selectedPeriod]);

  // Í±∞ÎûòÎüâ Ìè¨Îß∑ÌÑ∞
  const formatVolume = (value) => {
    if (value >= 1000000) {
      return `${(value / 1000000).toFixed(1)}M`;
    } else if (value >= 1000) {
      return `${(value / 1000).toFixed(1)}K`;
    }
    return value;
  };

  // Ï£ºÍ∞Ä Ìè¨Îß∑ÌÑ∞
  const formatPrice = (value) => {
    return `$${value}`;
  };

  // ÏãúÍ∞ÄÏ¥ùÏï° Ìè¨Îß∑ÌÑ∞
  const formatMarketCap = (value) => {
    if (value >= 1000000000000) {
      return `$${(value / 1000000000000).toFixed(2)}T`;
    } else if (value >= 1000000000) {
      return `$${(value / 1000000000).toFixed(2)}B`;
    } else if (value >= 1000000) {
      return `$${(value / 1000000).toFixed(2)}M`;
    } else if (value >= 1000) {
      return `$${(value / 1000).toFixed(2)}K`;
    }
    return `$${value}`;
  };

  // Ï£ºÏãùÏàò Ìè¨Îß∑ÌÑ∞
  const formatShares = (value) => {
    if (value >= 1000000000) {
      return `${(value / 1000000000).toFixed(2)}B`;
    } else if (value >= 1000000) {
      return `${(value / 1000000).toFixed(2)}M`;
    } else if (value >= 1000) {
      return `${(value / 1000).toFixed(2)}K`;
    }
    return value;
  };

  // Ïª§Ïä§ÌÖÄ Ìà¥ÌåÅ
  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div className="stock-chart-tooltip">
          <p className="tooltip-label">{`ÎÇ†Ïßú: ${label}`}</p>
          {payload.map((entry, index) => (
            <p key={index} style={{ color: entry.color }}>
              {`${entry.name}: ${entry.name === 'volume' ? formatVolume(entry.value) : `$${entry.value.toFixed(3)}`}`}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  if (loading) {
    return <div className="stock-chart-loading">Ï∞®Ìä∏ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</div>;
  }

  if (error) {
    return <div className="stock-chart-error">{error}</div>;
  }

  // Í∏∞Ï°¥ÏóêÎäî Î™®Îì† ÎÇ¥Ïö©ÏùÑ stock-chart-containerÎ°ú Í∞êÏååÏúºÎÇò, Ïù¥Ï†ú Í∞ÅÍ∞ÅÏùò ÏöîÏÜåÎ•º Í∞úÎ≥ÑÏ†ÅÏúºÎ°ú Î†åÎçîÎßÅ
  return (
    <>
      {/* Ï∞®Ìä∏ ÏöîÏïΩ Ï†ïÎ≥¥ */}
      {chartSummary && (
        <div className="chart-summary">
          <div className="summary-grid summary-grid-4rows">
            {/* 1Ìñâ: ÌòÑÏû¨Í∞Ä-ÌèâÍ∑†Í±∞ÎûòÎüâ-52Ï£º ÏµúÍ≥†Í∞Ä-1M Î≥ÄÎèôÏÑ± */}
            <div className="summary-item">
              <span className="summary-label">ÌòÑÏû¨Í∞Ä:</span>
              <span className="summary-value">${chartSummary.current_price || chartSummary.end_price}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">ÌèâÍ∑†Í±∞ÎûòÎüâ(60Ïùº):</span>
              <span className="summary-value">{chartSummary.avg_volume_60d ? formatVolume(chartSummary.avg_volume_60d) : 'N/A'}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">52Ï£º ÏµúÍ≥†Í∞Ä:</span>
              <span className="summary-value">${chartSummary.week_52_high || 'N/A'}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">1MÎ≥ÄÎèôÏÑ±:</span>
              <span className="summary-value">{chartSummary.volatility_1m ? `${chartSummary.volatility_1m}%` : 'N/A'}</span>
            </div>
            {/* 2Ìñâ: ÏãúÍ∞ÄÏ¥ùÏï°-Ïú†ÎèôÏ£ºÏãùÏàò-52Ï£º ÏµúÏ†ÄÍ∞Ä-1YÎ≥ÄÎèôÏÑ± */}
            <div className="summary-item">
              <span className="summary-label">ÏãúÍ∞ÄÏ¥ùÏï°:</span>
              <span className="summary-value">{chartSummary.market_cap ? formatMarketCap(chartSummary.market_cap) : 'N/A'}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">Ïú†ÎèôÏ£ºÏãùÏàò:</span>
              <span className="summary-value">{chartSummary.float_shares ? formatShares(chartSummary.float_shares) : 'N/A'}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">52Ï£º ÏµúÏ†ÄÍ∞Ä:</span>
              <span className="summary-value">${chartSummary.week_52_low || 'N/A'}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">1YÎ≥ÄÎèôÏÑ±:</span>
              <span className="summary-value">{chartSummary.volatility_1y ? `${chartSummary.volatility_1y}%` : 'N/A'}</span>
            </div>
          </div>
        </div>
      )}
      {/* Í∏∞Í∞Ñ Î≤ÑÌäº Ïª®Ìä∏Î°§ ÏÑπÏÖò */}
      <div className="stock-chart-controls">
        <div className="control-buttons control-buttons-right">
          {periodOptions.map(option => (
            <button
              key={option.value}
              className={`control-btn period-btn${selectedPeriod === option.value ? ' active' : ''}`}
              onClick={() => setSelectedPeriod(option.value)}
            >
              {option.label}
            </button>
          ))}
        </div>
      </div>
      {/* Ï∞®Ìä∏ */}
      <div className="stock-chart-wrapper">
        {chartData.length > 0 ? (
          <ResponsiveContainer width="103%" height={400}>
            <ComposedChart data={chartData} margin={{ top: 20, right: 80, bottom: 20, left: 20 }}>
              <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
              <XAxis 
                dataKey="date" 
                tick={{ fontSize: 12 }}
                tickFormatter={(value) => {
                  const date = new Date(value);
                  return `${date.getMonth() + 1}/${date.getDate()}`;
                }}
              />

              {/* Ï£ºÍ∞ÄÏö© YÏ∂ï (ÏôºÏ™Ω) */}
              <YAxis 
                yAxisId="price" 
                orientation="left"
                tick={{ fontSize: 12 }}
                tickFormatter={formatPrice}
                label={{ value: 'Ï£ºÍ∞Ä ($)', angle: -90, position: 'insideLeft' }}
              />

              {/* Í±∞ÎûòÎüâÏö© YÏ∂ï (Ïò§Î•∏Ï™Ω) - Í±∞ÎûòÎüâÏù¥ ÏÑ†ÌÉùÎêú Í≤ΩÏö∞ */}
              <YAxis 
                yAxisId="volume" 
                orientation="right"
                tick={{ fontSize: 12 }}
                tickFormatter={formatVolume}
                label={{ value: 'Í±∞ÎûòÎüâ', angle: 90, position: 'insideRight' }}
              />

              <Tooltip content={<CustomTooltip />} />
              {/* <Legend /> */}

              {/* Ï£ºÍ∞Ä ÎùºÏù∏ */}
              <Line
                yAxisId="price"
                type="monotone"
                dataKey="close"
                stroke="#EFC352"
                strokeWidth={2}
                dot={false}
                name="Ï¢ÖÍ∞Ä"
              />

              {/* Í±∞ÎûòÎüâ Î∞î Ï∞®Ìä∏ */}
              <Bar
                yAxisId="volume"
                dataKey="volume"
                fill="#988A7C"
                opacity={0.6}
                name="Í±∞ÎûòÎüâ"
              />
            </ComposedChart>
          </ResponsiveContainer>
        ) : (
          <div className="no-chart-data">Ï∞®Ìä∏ Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.</div>
        )}
      </div>
    </>
  );
};

export default StockChart;
